function littledino_fonts_url() {
	$fonts_url = '';

	/* Translators: If there are characters in your language that are not
	* supported by Nunito, translate this to 'off'. Do not translate
	* into your own language.
	*/
	$nunito = esc_html_x( 'on', 'Nunito font: on or off', 'littledino' );	

	/* Translators: If there are characters in your language that are not
	* supported by Muli, translate this to 'off'. Do not translate
	* into your own language.
	*/
	$muli = esc_html_x( 'on', 'Muli font: on or off', 'littledino' );

	/* Translators: If there are characters in your language that are not
	* supported by Open Sans, translate this to 'off'. Do not translate
	* into your own language.
	*/
	$open_sans = esc_html_x( 'on', 'Open Sans font: on or off', 'littledino' );

	if ('off' !== $nunito || 'off' !== $open_sans || 'off' !== $muli) {
		$font_families = array();

		if ( 'off' !== $nunito ) {
			$font_families[] = 'Nunito:900';
		}

		if ( 'off' !== $muli ) {
			$font_families[] = 'Muli:400,600,700,800,900';
		} 

		if ( 'off' !== $open_sans ) {
			$font_families[] = 'Open Sans:400,600,700';
		}

		$query_args = array(
			'family' => urlencode( implode( '|', $font_families ) ),
			'subset' => urlencode( 'latin,latin-ext' ),
		);

		$fonts_url = add_query_arg( $query_args, 'https://fonts.googleapis.com/css' );
	}
	
	return esc_url_raw( $fonts_url );
}

function editor_google_fonts() {
	wp_enqueue_style( 'editor-google-fonts', littledino_fonts_url(), array(), '1.0.0' );
}

add_action( 'admin_enqueue_scripts', 'editor_google_fonts' );

if (  !class_exists( 'LittleDino_Core_Public' ) ) {

	function littledino_default_fonts() {
		wp_enqueue_style( 'littledino-default-font', littledino_fonts_url(), array(), '1.0.0' );
	}
	
	add_action( 'wp_enqueue_scripts', 'littledino_default_fonts' );

}

